sp_widget.client_script:
function($scope, $sce) {

	var c = this;
	$scope.data.serviceDetails = $sce.trustAsHtml($scope.data.serviceDetails);

	c.getHTML = function(field) {
		return $sce.trustAsHtml(field);
	}

}

